# define matrix A, B and C
A &lt;- matrix(c(1, 2, 3, 4), nrow = 2, byrow = TRUE)
B &lt;- matrix(c(1, 0, 0, 1), nrow = 2, byrow = TRUE)
C &lt;- matrix(c(1, 0, 0, 2), nrow = 2, byrow = TRUE)

# a)
dim(A)

